CREATE TEMPORARY TABLE temp_pol (id int, color varchar(50), picture varchar(500), info varchar(500));

UPDATE pol_all INNER JOIN temp_pol ON temp_pol.id = pol_all.id
SET pol_all.color = temp_pol.color;

UPDATE pol_all INNER JOIN temp_pol ON temp_pol.id = pol_all.id
SET pol_all.picture = temp_pol.picture;

UPDATE pol_all INNER JOIN temp_pol ON temp_pol.id = pol_all.id
SET pol_all.info = temp_pol.info;


